import "./app.css"

import { initializeApp } from "firebase/app";
import { createUserWithEmailAndPassword, getAuth } from "firebase/auth"
import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
function Signup() {
    const [name, setName] = useState("")
    const [email, setEmail] = useState("")
    const [pswd, setPswd] = useState("")
    const [cpswd, setCpswd] = useState("")

    const navigate = useNavigate()



    const firebaseConfig = {
        apiKey: "AIzaSyClEH1wG_p4Bk7y8dl88UEi1OcSmd_7Yl4",
        authDomain: "pro-app-7adb5.firebaseapp.com",
        projectId: "pro-app-7adb5",
        storageBucket: "pro-app-7adb5.appspot.com",
        messagingSenderId: "840528707586",
        appId: "1:840528707586:web:759e54f6d5ecf02251671b",
        measurementId: "G-Q000JTLWD8"
    };

    const app = initializeApp(firebaseConfig);
    const auth = getAuth()
    const submitData = (event) => {
        event.preventDefault()
        if (pswd !== cpswd) {
            alert("Password does not match....")
        }

        let obj = {
            Email: email,
            password: pswd

        }
        createUserWithEmailAndPassword(auth, obj.Email, obj.password)
            .then(() => {
                alert("Registred successfully")
                navigate("/Login")
            })
            .catch(() => {
                alert("error.")
            })
    }


    return (
        <div  style={{backgroundColor:"blue"}}>
        <div>
            <div className="container" style={{ width: "400px", marginTop: "100px" }}>
                <div class="card" id="g" >
                    <div class="card-body">

                        <form onSubmit={submitData}>
                            <h2 style={{ textAlign: "center", }}>Signup</h2>
                            <div class="mb-3">
                                <label class="form-label">Name</label>
                                <input type="text" required class="form-control" value={name} onChange={e => setName(e.target.value)}  ></input>
                            </div>
                            <div class="mb-3">
                                <label class="form-label">Email address</label>
                                <input type="email" required class="form-control" value={email} onChange={e => setEmail(e.target.value)} ></input>
                            </div>
                            <div class="mb-3">
                                <label class="form-label">Password</label>
                                <input type="password" required class="form-control" value={pswd} onChange={e => setPswd(e.target.value)}  ></input>
                            </div>
                            <div class="mb-3">
                                <label class="form-label">Confirm Password</label>
                                <input type="password" required class="form-control" value={cpswd} onChange={e => setCpswd(e.target.value)}  ></input>
                            </div>

                            <button type="submit" class="btn btn-primary">Register</button>
                        </form>
                        <br></br>
                        <a >If you have  an account 
                        <Link to="/Login" style={{color:"white", textDecorationLine:"none", marginLeft:"10px",fontSize:"larger"}}>Login here</Link>
                        </a>

                    </div>
                </div>
            </div>
        </div>
        </div>

    )
}
export default Signup;
