import { useEffect, useState } from "react";
import { Link } from "react-router-dom";


function Addcart() {
    const [data, setData] = useState([])
    useEffect(() => {
        fetch("http://localhost:3002/cart")
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                setData(resp)
                console.log(resp)
            })
    },[])


    const deleteData = (id) => {
        fetch("http://localhost:3002/cart/" + id, {
            method: "DELETE"
        })
            .then(() => {
                
                window.location.reload()
            })
            .catch((err) => {
                alert("errror")
            })

    }


    return (
          <div>
             <div >
             <div class="nav" >
                <div  >
                    <img style={{ width: "70px", borderRadius: "60%" }} src="https://img.freepik.com/free-vector/gradient-mobile-store-logo-design_23-2149699848.jpg?w=740&t=st=1701680943~exp=1701681543~hmac=d2e55ed6996db84900ba6cfe1043d1ea415add3e10202d64e47745cee5d251c1"></img>
                </div>
                <div style={{ marginTop: "10px", }} >
                    <h3> <span style={{ color: "purple" }}> Mobile</span><span style={{ color: "pink" }}> Store</span></h3>
                </div>
                
                <div >
                    <Link to="/cart">  <i style={{ border: "1px solid black ", borderRadius: "50%", padding: "10px", marginTop: "10px", cursor: "pointer" }} class="fa-solid fa-cart-shopping"></i></Link>

                </div>
                <Link to="/Main">
        <button className="btn btn-secondary">back</button>
    </Link>
   
              
            </div>

          </div>
        <div > 
        <div id="car" >
           {data.map((ourdata) => (
                <div>
                    <div className="card" style={{ width: "250px", height: "400px", boxShadow: "white 1px 1px 1px 1px" }} >
                        <img src={ourdata.img} alt="k" style={{ width: "220px", height: "200px", borderRadius: "8px", marginLeft: "14px", marginTop: "20px" }} />
                        <h2>{ourdata.id}</h2>
                        <h4>{ourdata.name}</h4>
                        <h5>{ourdata.brand}</h5>
                        <h5  > <b style={{backgroundColor:"lightblue",width:"200px",borderRadius:"5px", padding:"5px"}}>${ourdata.price}</b> </h5>
                        <a className="btn btn-danger" onClick={() => { deleteData(ourdata.id) }}>Delete</a>

                        
                    </div>
                </div>
            ))}
     
            
    
        </div>
        </div>
        </div>
    )

}

export default Addcart;
