import { useEffect, useState } from "react"
import { Link, useParams } from "react-router-dom"



function Details(){
   
 let  {id}=useParams()

const [det,setDet]=useState()

    useEffect(() => {
        fetch("http://localhost:3002/commerce/" + id)
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                console.log(resp)
                setDet(resp)
            })
    }, [])

    return(
    
        <div>

        <div >
        <div class="nav" >
            <div  >
                <img style={{ width: "70px", borderRadius: "60%" }} src="https://img.freepik.com/free-vector/gradient-mobile-store-logo-design_23-2149699848.jpg?w=740&t=st=1701680943~exp=1701681543~hmac=d2e55ed6996db84900ba6cfe1043d1ea415add3e10202d64e47745cee5d251c1"></img>
            </div>
            <div style={{ marginTop: "10px", }} >
                <h3> <span style={{ color: "purple" }}> Mobile</span><span style={{ color: "pink" }}> Store</span></h3>
            </div>
            <div>  <h1 >Details For The Prodect</h1>
            </div>
            <div style={{marginRight:"200px"}}>

                <Link to="/Main">
                    <button className="btn btn-secondary">back</button>
                </Link>
            </div>

        </div>


    </div>


    <br></br>
    <br></br>

          
               {det &&
               <div>
               <img src={det.img} style={{ width: "200px", height: "150px"}} />
               <h5>{det.name}</h5> 
               <h5>prodect price:  <i class="fa-solid fa-indian-rupee-sign"></i>  {det.price}</h5>
               <br/>
               <h5>{det.details}</h5>
               <h5>{det.p}</h5>
               </div>
               }
        </div>
    )
}
export default Details;
