import { initializeApp } from "firebase/app";
import { signOut, getAuth } from "firebase/auth";
import {  useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import "./app.css"

function Main() {
    const [data, setData] = useState([])
    const [value, setValue] = useState("")
    const navigate = useNavigate()


    const firebaseConfig = {
        apiKey: "AIzaSyClEH1wG_p4Bk7y8dl88UEi1OcSmd_7Yl4",
        authDomain: "pro-app-7adb5.firebaseapp.com",
        projectId: "pro-app-7adb5",
        storageBucket: "pro-app-7adb5.appspot.com",
        messagingSenderId: "840528707586",
        appId: "1:840528707586:web:759e54f6d5ecf02251671b",
        measurementId: "G-Q000JTLWD8"
    };


    const app = initializeApp(firebaseConfig);
    const auth = getAuth()



    const submitData = (event) => {
        event.preventDefault()


        signOut(auth)
            .then(() => {
                alert("Logout successfull")
                navigate("/Login")
            })
            .catch(() => {
                alert("error")
            })
    }



    useEffect(() => {
        fetch("http://localhost:3002/commerce")
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                setData(resp)
            })
    }, [])



    const search = async (event) => {
        event.preventDefault()
        return await axios.get("http://localhost:3002/commerce?q={value}")
            .then((res) => {
                setData(res.data)
            })
            .catch((err) => {
                console.log(err)
            })
    }


    return (


        <div >




            <div class="nav">
                <div class="item"><img style={{ width: "70px", borderRadius: "20%" }} src="https://img.freepik.com/free-vector/gradient-mobile-store-logo-design_23-2149699848.jpg?w=740&t=st=1701649453~exp=1701650053~hmac=d9a0fd3b3f1c12509899ed83947437ddd4c32341b2c5c6de50ff2e25b4113681" alt="icon"></img></div>
                <div class="item" style={{color:"rgb(246, 13, 199)", marginTop:"15px"}}><b>MOBILE STORE</b></div>
                <div class="item"> <form class="d-flex"  onSubmit={search} >
                    <input value={value} onChange={(e) => setValue(e.target.value)} class="form-control me-2" type="search" placeholder="Search" aria-label="Search" style={{ width: "500px" }} />
                    <button class="btn btn-outline-success" type="submit">Search</button>
                </form></div>
                <div class="item"><i class="fa-solid fa-bell fa-shake" style={{ border: "1px solid black ", borderRadius: "50%", padding: "10px", cursor:"pointer" }}></i></div>
                <div class="item"> <form id="log" onSubmit={submitData}>
                    <button type="submit" class="btn btn-danger">Logout</button>
                </form></div>
            </div>



            <div id="con" style={{ display: "flex", gap: "20px" }}>
                <div>

                    <form onSubmit={submitData} style={{ backgroundColor: "light white", width: "330px", padding: "10px", marginTop: "40px" }}>


                        <div className="mb-3" style={{ width: "300px" }}>
                            <select id="disabledSelect" class="form-select">
                                <option >shop by consern</option>
                            </select>
                        </div>

                        <div className="mb-3" style={{ width: "300px" }}>
                            <select id="disabledSelect" class="form-select">
                                <option>Shop by category</option>
                            </select>
                        </div>

                        <div className="mb-3" style={{ width: "300px" }}>
                            <select id="disabledSelect" class="form-select">
                                <option>Shop by product</option>
                            </select>
                        </div>

                        <div className="mb-3" style={{ width: "300px" }}>
                            <select id="disabledSelect" class="form-select">
                                <option>Shop by all</option>
                            </select>
                        </div>


                        <div className="mb-3" style={{ width: "300px" }}>
                            <select id="disabledSelect" class="form-select">
                                <option>sort</option>
                                <option>name</option>
                                <option>brands</option>
                                <option>price </option>
                            </select>
                        </div>

                    </form>
                </div>



                <div  style={{ display: "grid", gridTemplateColumns: "auto auto  auto auto", gap: "50px", textAlign: "center" }}>
                    {data.map((ourdata) => (
                        <div>
                            <div id="gri" className="card" style={{ backgroundColor:" rgb(141, 223, 18)", width: "250px", height: "410px", marginTop: "17px" }} >
                                <img src={ourdata.img} style={{ width: "220px", height: "200px", borderRadius: "8px", marginLeft: "14px", marginTop: "20px" }} />
                                <h3><b> {ourdata.name}  </b></h3>
                                <h5> <b> {ourdata.brand}  </b> </h5>
                                <h5 >price:  <b style={{backgroundColor:"aqua", borderRadius:"20px"}}> {ourdata.price}  </b></h5>
                                <p className="btn btn-primary">ADD CART</p>
                            </div>
                        </div>

                    ))}
                </div>
            </div>
        </div>






    )
}
export default Main;
