import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./app.css"


function Addcart() {
    const [data, setData] = useState([])
    const [total, setTotal] = useState(0)
    useEffect(() => {
        fetch("http://localhost:3002/cart")
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                setData(resp)
                console.log(resp)
            })
    }, [])


    const deleteData = (id) => {
        fetch("http://localhost:3002/cart/" + id, {
            method: "DELETE"
        })
            .then(() => {

                window.location.reload()
            })
            .catch((err) => {
                alert("errror")
            })

    }

    const search = async (event) => {
        event.preventDefault()
        return await axios.get(`http://localhost:3002/commerce?q=${value}`)
            .then((res) => {
                setData(res.data)
                setValue("")
            })
            .catch((err) => {
                alert("error")
            })

    }




    
    const reset = (() => {
        fetch("http://localhost:3002/commerce")
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                setData(resp)

            })

    })






    useEffect(() => {
        fetch("http://localhost:3002/cart")
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                setData(resp)

                const total = resp.reduce((acc, item) => acc + parseFloat(item.price), 0)
                setTotal(total)
            })
    },[])








    return (
        <div>
            <div >
                <div class="nav" >
                    <div  >
                        <img style={{ width: "70px", borderRadius: "60%" }} src="https://img.freepik.com/free-vector/gradient-mobile-store-logo-design_23-2149699848.jpg?w=740&t=st=1701680943~exp=1701681543~hmac=d2e55ed6996db84900ba6cfe1043d1ea415add3e10202d64e47745cee5d251c1"></img>
                    </div>
                    <div style={{ marginTop: "10px", }} >
                        <h3> <span style={{ color: "purple" }}> Mobile</span><span style={{ color: "pink" }}> Store</span></h3>
                    </div>
                    <div >
                    <form onSubmit={search} class="d-flex" role="search" style={{ width: "500px", marginTop: "10px", cursor: "pointer" }}>
                        <input value={value} onChange={change} class="form-control me-2" type="search" placeholder="Search" />
                        <button class="btn btn-primary" type="submit">Search</button>
                        <i type="reset" onClick={reset} style={{ marginTop: "10px", marginLeft: "20px", cursor: "pointer" }} class="fa-solid fa-xmark"></i>
                    </form>
                </div>

                    <div >
                        <Link to="/cart">  <i style={{ border: "1px solid black ", borderRadius: "50%", padding: "10px", marginTop: "10px", cursor: "pointer" }} class="fa-solid fa-cart-shopping"></i></Link>



                    </div>
                    <div>
                        Total Price:<h2>{total} </h2>
                    </div>
                    <Link to="/Main">
                        <button className="btn btn-secondary">back</button>
                    </Link>
                </div>


            </div>


            <br></br>
            <br></br>
            
            <div >
                <div>


                    <table class="table">
                        <thead>
                            <tr>
                                <th scope="col">ID</th>
                                <th scope="col">Img</th>
                                <th scope="col">Mobile_Name</th>
                                <th scope="col">Price</th>
                                <th scope="col">Remove</th>
                                <th scope="col">More about <br></br>the product</th>
                               
                          
                            </tr>
                        </thead>


                        {data.map((ourdata) => (
                           


                           
                            <tbody>
                                <tr>
                                <th scope="row">{ourdata.id}</th>
                                <th scope="row"><img src={ourdata.img} alt="k" style={{ width: "100px", height: "90px", borderRadius: "8px" }} /></th>
                                <th scope="row">{ourdata.name}</th>
                              
                               
                                <th scope="row"><i class="fa-solid fa-indian-rupee-sign"></i> {ourdata.price}</th>
                                <th scope="row"><p className="btn btn-danger" onClick={() => { deleteData(ourdata.id) }}>Remove</p></th>
                                <th scope="row">
                                    <Link to="/Details">
                                        <p className="btn btn-success">Details</p>
                                    </Link>
                                </th>
                            </tr>
            </tbody>

                           
                     


                        ))}
                          </table>


                </div>
            </div>
        </div>
    )

}

export default Addcart;
