// Const [state,setState]=useState("vijay")
function State(){
  const [id,setId]=useState("01")
  const [sur,setSur]=useState("Thummuru")
  const [name,setName]=useState("Nishanth")
  const [city,setCity]=useState("bengaluru")
  const [value,setValue]=useState("0")

  const change=()=>{
    setId("02")
    setSur("T")
    setName("Nikhil")
    setCity("Bangalore")
  
  }
  const increment=()=>{
    setValue(value+1)
  }
  const decrement=() =>{
    setValue(value-1)
  }
  return(
    <div>
      <h1>we are using useState()</h1>
      <h2>Count:{value}</h2>
      <button onClick={increment}>INC</button>
      <button onClick={decrement}>DEC</button>
       <State2>  id={id} sur= {sur} name={name} city={city}  </State2>  
      <button onClick={change}>Update</button>
    </div>
  )
}
export default State;


