import { useEffect, useState } from "react"
import { Link, useParams } from "react-router-dom"



function Details(){
   
 let  {empid}=useParams()

const [item,setItem]=useState()

useEffect(() => {
    fetch("http://localhost:3002/commerce")
        .then((res) => {
            return res.json()
        })
        .then((resp) => {
            setData(resp)

        })

}, [])

    return(
    
        <div>
            <Link to="/Main">
                 <p style={{marginTop:"10px"}} className="btn btn-danger">Back To Home </p>
            </Link>
            
            <h1>Details For The Prodect</h1>
               {item &&
               <div>
               <img src={item.img} style={{ width: "200px", height: "150px"}} />
               <h5>{item.name}</h5> 
               <h5>prodect price:  <i class="fa-solid fa-indian-rupee-sign"></i>  {item.price}</h5>
               <br/>
               <h5>{item.details}</h5>
               <h5>{item.price}</h5>
               </div>
               }
        </div>
    )
}
export default Details;
