// import "./app.css"

import axios from "axios";
import { initializeApp } from "firebase/app";
import { signOut, getAuth } from "firebase/auth"
import { useEffect, useState } from "react";
// import { useState } from "react";
import { useNavigate } from "react-router-dom";
function Main() {

    const [value, setValue] = useState("")
    const [data, setData] = useState([])
    const navigate = useNavigate()




    const firebaseConfig = {
        apiKey: "AIzaSyClEH1wG_p4Bk7y8dl88UEi1OcSmd_7Yl4",
        authDomain: "pro-app-7adb5.firebaseapp.com",
        projectId: "pro-app-7adb5",
        storageBucket: "pro-app-7adb5.appspot.com",
        messagingSenderId: "840528707586",
        appId: "1:840528707586:web:759e54f6d5ecf02251671b",
        measurementId: "G-Q000JTLWD8"
    };

    const app = initializeApp(firebaseConfig);
    const auth = getAuth()

    const submitData = (event) => {
        event.preventDefault()

        signOut(auth)
            .then(() => {
                alert("Logout successfully")
                navigate("/Logout")
            })
            .catch(() => {
                alert("error.")
            })
    }

    useEffect(() => {
        fetch("http://localhost:3002/commerce")
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                setData(resp)

            })

    }, [])

    const change = (e) => {
        setValue(e.target.value)
    }
    const search = async (event) => {
        event.preventDefault()
        return await axios.get(`http://localhost:3002/commerce?q=${value}`)
            .then((res) => {
                setData(res.data)
                setValue("")
            })
            .catch((err) => {
                alert("error")
            })

    }

    const reset = (() => {
        fetch("http://localhost:3002/commerce")
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                setData(resp)

            })

    })


    let option = ["id", "name", "brand", "price"]
    const [sort, setSort] = useState()
    const SortData = async (e) => {
        console.log("clicked")
        e.preventDefault()
        let value = e.target.value
        setSort(value)
        return await axios.get(`http://localhost:3002/commerce?_sort=${value}$_order=asc`)
            .then((res) => {
                setData(res.data)

            })
            .catch((err) => {
                alert("error")
            })
    }



    return (


        <div >
            <div  style={{display:"flex", width: "100%", position: "fixed", padding: "10px", gap: "100px", boxShadow: "1px 1px 1px 1px", backgroundColor: "white" }}>
                <div  >
                    <img style={{ marginTop:"10px",width: "70px", borderRadius: "60%" }} src="https://img.freepik.com/free-vector/gradient-mobile-store-logo-design_23-2149699848.jpg?w=740&t=st=1701680943~exp=1701681543~hmac=d2e55ed6996db84900ba6cfe1043d1ea415add3e10202d64e47745cee5d251c1"></img>
                </div>
                <div  >
                    <h3> <span style={{ color: "purple" }}> Mobile</span><span style={{ color: "pink" }}> Store</span></h3>
                </div>
                <div >
                    <form onSubmit={search} class="d-flex" role="search" style={{ width: "500px",  cursor: "pointer" }}>
                        <input value={value} onChange={change} class="form-control me-2" type="search" placeholder="Search" aria-label="Search" />
                        <button class="btn btn-primary" type="submit">Search</button>
                        <i type="reset" onClick={reset} style={{ marginTop: "10px", marginLeft: "20px", cursor: "pointer" }} class="fa-solid fa-spinner"></i>
                    </form>
                </div>
                <div >
                    <i style={{ border: "1px solid black ", borderRadius: "50%", padding: "10px", cursor: "pointer" }} class="fa-solid fa-cart-shopping"></i>

                </div>

                <div>
                    <form onSubmit={submitData}>
                        <button type="submit" class="btn btn-danger">Logout</button>
                    </form>

                </div>
            </div>

            <br></br>
            <br></br>
            <br></br>
            <br></br>
            <br></br>


            <div class="parent" style={{ display: "flex" }}>
                <div class="child1" >
                    <form onSubmit={submitData} style={{ backgroundColor: "light white", width: "330px", padding: "10px", marginTop: "20px" }}>


                        <div className="mb-3" style={{ width: "300px" }}>
                            <select id="disabledSelect" class="form-select">
                                <option >shop by consern</option>
                            </select>
                        </div>

                        <div className="mb-3" style={{ width: "300px" }}>
                            <select id="disabledSelect" class="form-select">
                                <option>Shop by category</option>
                            </select>
                        </div>

                        <div className="mb-3" style={{ width: "300px" }}>
                            <select id="disabledSelect" class="form-select">
                                <option>Shop by product</option>
                            </select>
                        </div>

                        <div className="mb-5" style={{ width: "300px" }}>
                            <select id="disabledSelect" class="form-select">
                                <option>Shop by all</option>
                            </select>
                        </div>


                        <div className="mb-3" >
                            <select style={{ width: "300px", padding: "6px", borderRadius: "5px" }} value={sort} onChange={SortData}>
                                <option selected >Select</option>
                                {option.map((item) => {
                                    <option value={item}> {item}</option>
                                })}
                            </select>

                        </div>

                    </form>






                </div>


                <div class="child2" id="bu" style={{   display: "grid", gridTemplateColumns: "auto auto  auto auto", gap: "30px" }} >
                    {data.map((item) => (






                        <div style={{ marginTop: "5px", borderRadius: "10px", backgroundColor: "white", width: "16rem", padding: "5px", paddingLeft: "20px" }}>
                            <img style={{ width: "180px" }} src={item.img} alt="." />
                            <h4>{item.id}.</h4>
                            <h3><b>{item.name}</b></h3>
                            <h5> <b>{item.brand}</b> </h5>
                            <h5>price:<b>{item.price}</b></h5>
                            <h5> <span className="btn btn" style={{ width: "100px", backgroundColor: "black", color: "white" }}> Details</span>
                                <span className="btn btn-primary" style={{ marginLeft: "15px", width: "100px" }}>ADD CART</span>
                            </h5>
                        </div>





                    ))}






                </div>



            </div>


        </div>











    )
}
export default Main;
