import { useState } from "react";

function State2({blue, yel, onn, off}){
    const[color,setColor]=useState(blue)
    const[data,setData]=useState(onn)
    const show=()=>{
        let x= color==blue ? yel : blue
        setColor(x)
    }
    const up=()=>{
        let a= data==onn ? off : onn
        setData(a)
    }
        return(
        <div>
  
         <div onClick={show} style={{backgroundColor:color, borderRadius:"50%", width:"300px", height:"300px"}}>
        
         </div>
         <button onClick={up}>{data}</button>

        </div>
    )
  }
  
  
  
  export default State2;
